//     search(username).then((data) => {
//       if (data.token ==null) {
//         setLoading(false);
//         return setErrorMessage(data.status);
//       }
//       else{
//       }
//       setLoading(false);
//     });
//   };
class SearchPage extends Component{
    
    render(){
        return (
                <Div0>
                    <Header></Header>
                    <ContentDiv>
                        <SearchDiv>
                            <SearchTitle>請輸入欲搜尋使用者：</SearchTitle>
                            <DivLog>
                            <InPutL id="email" type="text" placeholder="USERNAME" size="30" ></InPutL>
                            </DivLog>
                            {/* <DivLogin type="button" className="btn btn-primary" value={loading ? '搜尋中...' : '搜尋'} onClick={handleSearch} disabled={loading}> */}
                            {/* </DivLogin> */}
                        </SearchDiv>
                    </ContentDiv>
                </Div0>
        )}}
export default SearchPage;
