import React from "react";
import { Div0,Logo,HeaderList,LogoText,BesideTop,BesideMid,BesideBot } from "./style";
import './sty.css'
const BesideInner = () => {
    var acc = document.getElementsByClassName("accordion");
    var i;
    function onChangeS1(value) {
        console.log(acc);
        console.log(i);
        for (i = 0; i < acc.length; i++) {
            acc[i].addEventListener("click", function() {
            this.classList.toggle("active");
            var panel = this.nextElementSibling;
            if (panel.style.maxHeight) {
                panel.style.maxHeight = null;
            } else {
                panel.style.maxHeight = panel.scrollHeight + "px";
            }
        });
        }
    }

    return(
        <Div0>
            <BesideTop>
                <Logo>
                    <img src='https://i.imgur.com/BJ21wvu.png'  height={56} width={87.5}></img>
                </Logo>
                <HeaderList>
                    <LogoText>
                        管理系統
                    </LogoText>
                </HeaderList>
            </BesideTop>
            <BesideMid>
            <button className="accordion"  onClick={(e)=>onChangeS1(e.target.value)}>Section 1</button>
            <div className="panel">
                <p>1.</p>
            </div>

            <button className="accordion"  onClick={(e)=>onChangeS1(e.target.value)}>Section 2</button>
            <div className="panel">
                <p>2</p>
            </div>

            <button className="accordion"  onClick={(e)=>onChangeS1(e.target.value)}>Section 3</button>
            <div className="panel">
                <p>3</p>
            </div>
            </BesideMid>
            <BesideBot>

            </BesideBot>
        </Div0>
    )
}
export default BesideInner;
