import styled from 'styled-components';
export const HeaderWrapper = styled.div`
    height: 70px;
    width: 100%;
    border-bottom: 3px solid #7c9c99;
    display: flex;
    overflow: hidden;
    min-width: 1200px;
    background-color:#7c9c99;

`;
export const HeaderList=styled.div`
    height: 70px;
    width: 100%;
    pedding-righ: 70px;
    box-sizing: border-box;
    margin: 0,auto;

`;
export const LogoText=styled.p`
    font-size:30px;
    color:white;
    font-family: 'Noto Sans TC', sans-serif;
    width: 120px;
    margin-top: 40px;
    margin-left: 3px;
    float: left;
`;
export const PeoLogo2=styled.div`
    float: right;
    width: 80px;
    border-left: 3px solid #000000;
    font-family: 'Noto Sans TC', sans-serif;
    display: flex;
    align-items: center;
`;
export const PeoLogo=styled.div`
    float: right;
    height: 50px;
    width: 60px;
    background-color:white;
    margin:10px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius:50px;
`;
