import styled from 'styled-components';
const NavItemHeight = 40;
const NavTitleHeight = 30;

export const Div0 = styled.div`
    width: 100%;
    height: 800px;
    margin: 0 auto;
    display:grid;
    grid-template-rows: 85px 1fr;

`
export const Title = styled.div`
    display:grid;
    grid-template:1fr / auto 1fr;
    border-bottom:gray 2px solid;
    padding-bottom:10px;
    align-items:end;
`
export const ChooseDiv = styled.div`
overflow:auto;
    &::-webkit-scrollbar {

        width: 7px;
  
      }
  
    &::-webkit-scrollbar-button {
  
        background: transparent;
  
        border-radius: 4px;
  
      }
  
    &::-webkit-scrollbar-track-piece {
  
        background: transparent;
  
      }
  
    &::-webkit-scrollbar-thumb {
  
        border-radius: 4px;
  
        background-color: rgba(0, 0, 0, 0.4);
  
        border: 1px solid slategrey;
  
      }
  
    &::-webkit-scrollbar-track {
  
        box-shadow: transparent;
  
      }
`
export const TitleText = styled.div`
    font-size:25px;
`
export const ChooseTitle =styled.div`
    height: ${NavTitleHeight}px;
    line-height: ${NavTitleHeight}px;
    padding: 0 20px;
    font-size: 16px;
    text-align: left;
    background-color: #f0efee;
    color: rgba(0, 0, 0, 0.5);
    font-weight: 600;
`
export const ChooseList = styled.div`
    padding: 10px
`
export const ChooseItem =styled.a`
        display: flex;
        height: ${NavItemHeight}px;
        padding: 0 10px;
        flex-direction: row;
        &:hover{
            background-color:cyan;
        }
        justify-content: flex-start;
        align-items: center;
`
