import React, { PureComponent ,useState } from 'react';
import { Div0,Div1 } from './style.js';
import { Line } from "react-chartjs-2";
import Chart from 'chart.js/auto';
// import {data1234} from './a.js'


// const request = require('request');
let data1234=[]
let da=[]
let label1=[]
// request('https://backend-111406.herokuapp.com/api/record/zsda5858sda', function (error, response, body) {
//     dataT=JSON.parse(body).then(dataT=>dataT.json());
//     console.log('a',dataT.times);

// });




// const DataDraw = () => {
//     return (
//     <Div0>
//         <Div1>
//             <Line data={data1234} />
//         </Div1>
//     </Div0>
//     );
// }
// export default DataDraw;


class DataDraw extends React.Component{
    constructor(props){
        super(props);
        this.state={
        "data":[]
    };
    }
    componentDidMount(){
    this.getItems();
    }
    getItems(){
    fetch('https://backend-111406.herokuapp.com/api/record/zsda5858sda')
    .then(results=>results.json())
    .then(results=>this.setState({"data":results.data}));
    
    data1234={
        labels:label1,
        datasets:[
            {
                label: "First dataset",
                data: da,
                fill: true,
                backgroundColor: "rgba(75,192,192,0.2)",
                borderColor: "rgba(75,192,192,1)",
                lineTension: 0.5
            }
        ]
    }
}
    
    render(){
        return (
            <Div0>
                <Div1>
                    {
                        this.state.data.map((item) =>(
                            da.push(item.times)
                        ))
                    }
                    {
                        this.state.data.map((item) =>(
                            label1.push(item.create_time)
                        ))
                    }
                    {
                        data1234={
                            labels:label1,
                            datasets:[
                                {
                                    label: "First dataset",
                                    data: da,
                                    fill: true,
                                    backgroundColor: "rgba(75,192,192,0.2)",
                                    borderColor: "rgba(75,192,192,1)",
                                    lineTension: 0.5
                                }
                            ]
                        }
                    }
                    <Line data={data1234}></Line>
                </Div1>
            </Div0>
    );
}
}
export default DataDraw;
