import styled from 'styled-components';
export const HeaderWrapper = styled.div`
    height: 70px;
    width: 100%;
    border-bottom: 3px solid #7c9c99;
    display: flex;
    overflow: hidden;
    min-width: 1200px;

`;
export const HeaderList=styled.div`
    height: 70px;
    width: 100%;
    pedding-righ: 70px;
    box-sizing: border-box;
    margin: 0,auto;

`;
export const LogoText=styled.p`
    font-size: 30px;
    color: #7c9c99;
    font-family: arial;
    width: 120px;
    margin-top: 40px;
    margin-left: 3px;
    float: left;
`;
export const PeoLogo=styled.div`
float: right;
height: 50px;
width: 50px;
display: flex;
margin-top: 15px;
`;
