import styled from 'styled-components';
const NavItemHeight = 40;
const NavTitleHeight = 30;

export const Div0 = styled.div`
    width: 100%;
    height: 100%;
    margin: 0 auto;
    display:grid;
    grid-template-rows: 85px 1fr;

`
export const Title = styled.div`
    display:grid;
    grid-template:1fr / auto 1fr;
    border-bottom:gray 2px solid;
    padding-bottom:10px;
    align-items:end;
`
export const ChooseDiv = styled.div`
`
export const TitleText = styled.div`
    font-size:25px;
`
export const ChooseTitle =styled.div`
    height: ${NavTitleHeight}px;
    line-height: ${NavTitleHeight}px;
    padding: 0 20px;
    font-size: 16px;
    text-align: left;
    background-color: #f0efee;
    color: rgba(0, 0, 0, 0.5);
    font-weight: 600;
`
export const ChooseList = styled.div`
    padding: 10px
`
export const Exit =styled.a`
display: flex;
height: ${NavItemHeight}px;
padding: 0 10px;
flex-direction: row;
&:hover{
    background-color:#445553;
    color:white;
    margin-left:10px;
    height: ${NavItemHeight+10}px;
}
justify-content: flex-start;
align-items: center;
`
export const ChooseItem =styled.a`
        display: flex;
        height: ${NavItemHeight}px;
        padding: 0 10px;
        flex-direction: row;
        &:hover{
            background-color:#445553;
            color:white;
            margin-left:10px;
            height: ${NavItemHeight+10}px;
        }
        justify-content: flex-start;
        align-items: center;
`
