/*=====================================
    SideBar(Only for UserView)

    Author: Frank
    CreateTime: 2022 / 05 / 18
=====================================*/

/*--------------------------
    Import
--------------------------*/
import React from "react";
import { createSelector } from "reselect";
import actionCreators from "../../actions/creators";
import useRedux from "../../actions/hooks/useRedux";
import styled from "styled-components";
import { GlobalStyle, Colors } from "../../stylecomponents";
import CupoyRouter from "../commons/router/CupoyRouter";
import CupoySrefView from "../commons/router/CupoySrefView";
import I18N from "../commons/I18N";
import influencerUtil from "../../util/influencerUtil";
import useAssistId from "../hooks/kwasst/useAssistId";
import memberUtil from "../../util/memberUtil";
/*--------------------------
    Variables
--------------------------*/
const SidebarNavWidth = 272;
// const ViewSpace = parseInt(GlobalStyle.ViewSpace, 10);
// const TopSpace =
//     parseInt(GlobalStyle.TopbarHeight, 10) +
//     parseInt(GlobalStyle.TopbarContainerSpace, 10);
const NavItemHeight = 40;
const NavTitleHeight = 25;
// const NavSettingBlock = 118;

/*--------------------------
    Styled
--------------------------*/
const SidebarNav = styled.nav`
    position: fixed;
    /* position: absolute; */
    top: 60px;
    /* top: 0; */
    width: ${SidebarNavWidth}px;
    min-width: ${SidebarNavWidth}px;
    bottom: 0;
    /* border-right: 1px solid #e1e1e1; */
    background-color: #fff;
    /* @media only screen and (max-width: ${GlobalStyle.NewIA_ContainerWidth -
    1}px) {
        top: ${GlobalStyle.TopbarHeight};
        bottom: 0;
    } */
`;

const SidebarNavContent = styled.nav`
    position: relative;

    > a {
        display: block;
    }
`;

const SidebarNavTitle = styled.div`
    height: ${NavTitleHeight}px;
    line-height: ${NavTitleHeight}px;
    padding: 0 20px;
    font-size: 16px;
    text-align: left;
    background-color: #f0efee;
    color: rgba(0, 0, 0, 0.5);
    font-weight: 600;

    /* &:not(:first-of-type) {
        border-top: 1px solid #e5e5e5;
    } */
`;

const SidebarNavList = styled.div`
    padding: 10px;
`;

const SidebarNavItemText = styled.div`
    font-size: 16px;
    font-weight: bold;
`;

const SidebarNavItemCount = styled.div`
    font-size: 12px;
`;

const SidebarNavItem = styled.div`
    display: flex;
    height: ${NavItemHeight}px;
    padding: 0 10px;
    flex-direction: row;
    justify-content: flex-start;
    align-items: center;
    margin: ${({ margin }) => (margin ? margin : "0")};

    ${(props) => {
        if (props.isActive) {
            return `
                color: ${Colors.Red};
                background-color: rgb(253, 241, 241);
                border-left: 5px solid ${Colors.Red};
                border-radius: 0 20px 20px 0;
            `;
        } else {
            return `
                color: #323030;
                background-color: #FFF;
                &:hover {
                    background-color: rgb(246, 245, 244);
                }
            `;
        }
    }}

    ${SidebarNavItemCount} {
        color: ${(props) => (props.isActive ? Colors.Red : Colors.Dark3)};
    }
`;

const SidebarNavItemIcon = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    margin-right: 18px;
    width: 16px;

    > i {
        font-size: ${({ iconSize }) => (iconSize ? `${iconSize}px` : "14px")};
    }

    > span {
        font-size: ${({ iconSize }) => (iconSize ? `${iconSize}px` : "14px")};
    }
`;

const selector = createSelector(
    (state) => state.member.isLogin,
    (state) => state.member.memberInfo,
    (state) => state.influencer.selfInfluencer,
    (state) => state.influencer.influencerMap,
    (state) => state.library,
    (state) => state.member.followNewsMatrix,
    (state) => state.collection.subscribeCumatrixSize,
    (state) => state.collection.buyOutCumatrixSize,
    (state) => state.collection.coeditorCumatrixSize,
    (state) => state.collection.selfCumatrixSize,
    (state) => state.collection.selfMarathonSize,
    (state) => state.order.countMap,
    (state) => state.club.allJoinedClubSize,
    (state) => state.expertBidding.expertBiddingCountInfo,
    (
        isLogin,
        memberInfo,
        selfInfluencer,
        influencerMap,
        library,
        followNewsMatrix,
        subscribeCumatrixSize,
        buyOutCumatrixSize,
        coeditorCumatrixSize,
        selfCumatrixSize,
        selfMarathonSize,
        countMap,
        allJoinedClubSize,
        expertBiddingCountInfo
    ) => ({
        isLogin,
        memberInfo,
        selfInfluencer,
        influencerMap,
        library,
        followNewsMatrix,
        subscribeCumatrixSize,
        buyOutCumatrixSize,
        coeditorCumatrixSize,
        selfCumatrixSize,
        selfMarathonSize,
        countMap,
        allJoinedClubSize,
        expertBiddingCountInfo,
    })
);

/*--------------------------
    Main Component
--------------------------*/
const Sidebar = (props) => {
    const [
        {
            // isLogin,
            memberInfo,
            selfInfluencer,
            // influencerMap,
            // library,
            // followNewsMatrix,
            // subscribeCumatrixSize,
            // buyOutCumatrixSize,
            // coeditorCumatrixSize,
            // selfCumatrixSize,
            // selfMarathonSize,
            // countMap,
            // allJoinedClubSize,
            // expertBiddingCountInfo
        },
    ] = useRedux(selector, {
        webActions: actionCreators.webActionCreators,
        memberActions: actionCreators.memberActionCreators,
        pointsActions: actionCreators.pointsActionCreators,
        influencerActions: actionCreators.influencerActionCreators,
        collectionActions: actionCreators.collectionActionCreators,
        kwassistActions: actionCreators.kwassistActionCreators,
        expPointActions: actionCreators.expPointActionCreators,
        orderActions: actionCreators.orderActionCreators,
        clubActions: actionCreators.clubActionCreators,
        expertBiddingActions: actionCreators.expertBiddingActionCreators,
    });

    const assistid = useAssistId();
    const { createStudyEvent } = memberUtil.getMemberStudyEventPrivilege(memberInfo);
    const isExpert = selfInfluencer
        ? influencerUtil.chkIsExpert(selfInfluencer.influencerType)
        : false;

    return (
        <SidebarNav>
            <SidebarNavContent>
                <CupoySrefView
                    // statename={CupoyRouter.STATES.MYPAGE_USER}
                    statename={CupoyRouter.STATES.USER}
                    params={{
                        mrscid: memberInfo.mrscid,
                    }}
                >
                    <SidebarNavItem
                        margin="10px 10px"
                        isActive={CupoyRouter.includes(CupoyRouter.STATES.USER)}
                    >
                        <SidebarNavItemIcon iconSize={20}>
                            <span className="material-icons">person</span>
                        </SidebarNavItemIcon>
                        <SidebarNavItemText>
                            <I18N>我的主頁</I18N>
                        </SidebarNavItemText>
                        {/* <SidebarNavItemMore>
                            <SidebarNavItemCount>
                                <NumberFormat number={ordercount} />
                            </SidebarNavItemCount>
                            <SidebarNavItemMoreIcon>
                                <i className="zmdi zmdi-chevron-right"></i>
                            </SidebarNavItemMoreIcon>
                        </SidebarNavItemMore> */}
                    </SidebarNavItem>
                </CupoySrefView>

                <SidebarNavTitle>
                    <I18N>購買</I18N>
                </SidebarNavTitle>

                <SidebarNavList>
                    <CupoySrefView
                        statename={CupoyRouter.STATES.MYPAGE_ORDER_LIST}
                    >
                        <SidebarNavItem
                            // margin="10px 10px 0"
                            isActive={CupoyRouter.includes(
                                CupoyRouter.STATES.MYPAGE_ORDER
                            )}
                        >
                            <SidebarNavItemIcon iconSize={20}>
                                <span className="material-icons-outlined">
                                    assignment
                                </span>
                            </SidebarNavItemIcon>
                            <SidebarNavItemText>
                                <I18N>我的訂單</I18N>
                            </SidebarNavItemText>
                            {/* <SidebarNavItemMore>
                                <SidebarNavItemCount>
                                    <NumberFormat number={ordercount} />
                                </SidebarNavItemCount>
                                <SidebarNavItemMoreIcon>
                                    <i className="zmdi zmdi-chevron-right"></i>
                                </SidebarNavItemMoreIcon>
                            </SidebarNavItemMore> */}
                        </SidebarNavItem>
                    </CupoySrefView>
                    <CupoySrefView
                        statename={CupoyRouter.STATES.MYPAGE_SUBSCRIBE_INFO}
                    >
                        <SidebarNavItem
                            // margin="10px 10px 0"
                            isActive={CupoyRouter.includes(
                                CupoyRouter.STATES.MYPAGE_SUBSCRIBE_INFO
                            )}
                        >
                            <SidebarNavItemIcon iconSize={20}>
                                <span className="material-icons-outlined">
                                    how_to_reg
                                </span>
                            </SidebarNavItemIcon>
                            <SidebarNavItemText>
                                <I18N>我的方案</I18N>
                            </SidebarNavItemText>
                            {/* <SidebarNavItemMore>
                                <SidebarNavItemCount>
                                    <NumberFormat number={ordercount} />
                                </SidebarNavItemCount>
                                <SidebarNavItemMoreIcon>
                                    <i className="zmdi zmdi-chevron-right"></i>
                                </SidebarNavItemMoreIcon>
                            </SidebarNavItemMore> */}
                        </SidebarNavItem>
                    </CupoySrefView>
                </SidebarNavList>

                <SidebarNavTitle>
                    <I18N>課程</I18N>
                </SidebarNavTitle>

                <SidebarNavList>
                    <CupoySrefView
                        statename={CupoyRouter.STATES.MYPAGE_MY_CURRICULUM}
                    >
                        <SidebarNavItem
                            isActive={CupoyRouter.isActive(
                                CupoyRouter.STATES.MYPAGE_MY_CURRICULUM
                            )}
                        >
                            <SidebarNavItemIcon iconSize={20}>
                                <i className="icon-book"></i>
                            </SidebarNavItemIcon>
                            <SidebarNavItemText>
                                <I18N>我的課程</I18N>
                            </SidebarNavItemText>
                            {/* <SidebarNavItemMore>
                                    <SidebarNavItemMoreIcon>
                                        <i className="zmdi zmdi-chevron-right"></i>
                                    </SidebarNavItemMoreIcon>
                                </SidebarNavItemMore> */}
                        </SidebarNavItem>
                    </CupoySrefView>
                    {/* <CupoySrefView
                        statename={
                            CupoyRouter.STATES.MYPAGE_COLLECTIONS_COEDITOR
                        }
                    > */}
                    {/* <SidebarNavItem
                    // isActive={CupoyRouter.isActive(
                    //     CupoyRouter.STATES
                    //         .MYPAGE_COLLECTIONS_COEDITOR
                    // )}
                    >
                        <SidebarNavItemIcon iconSize={20}>
                            <span className="material-icons-outlined">
                                comment_bank
                            </span>
                        </SidebarNavItemIcon>
                        <SidebarNavItemText>
                            <I18N>我的學習活動</I18N>
                        </SidebarNavItemText>
                        <SidebarNavItemMore>
                                <SidebarNavItemCount>
                                    {coeditorCumatrixSize > 0 && (
                                        <NumberFormat
                                            number={coeditorCumatrixSize}
                                            decimal={0}
                                        />
                                    )}
                                </SidebarNavItemCount>
                                <SidebarNavItemMoreIcon>
                                    <i className="zmdi zmdi-chevron-right"></i>
                                </SidebarNavItemMoreIcon>
                            </SidebarNavItemMore>
                    </SidebarNavItem> */}
                    {/* </CupoySrefView> */}
                    <CupoySrefView
                        statename={CupoyRouter.STATES.MYPAGE_MY_EVENT}>
                        <SidebarNavItem
                            isActive={CupoyRouter.isActive(
                                CupoyRouter.STATES.MYPAGE_MY_EVENT
                            )}
                        >
                            <SidebarNavItemIcon iconSize={20}>
                                <i className="icon-book"></i>
                            </SidebarNavItemIcon>
                            <SidebarNavItemText>
                                <I18N>我的學習活動</I18N>
                            </SidebarNavItemText>
                        </SidebarNavItem>
                    </CupoySrefView>
                    <CupoySrefView
                        statename={
                            CupoyRouter.STATES.MYPAGE_COLLECTIONS_MYSELFEDIT
                        }
                    >
                        <SidebarNavItem
                            isActive={CupoyRouter.isActive(
                                CupoyRouter.STATES.MYPAGE_COLLECTIONS_MYSELFEDIT
                            )}
                        >
                            <SidebarNavItemIcon iconSize={20}>
                                <span className="material-icons-outlined">
                                    library_books
                                </span>
                            </SidebarNavItemIcon>
                            <SidebarNavItemText>
                                <I18N>新增 / 編輯課程</I18N>
                            </SidebarNavItemText>
                            {/* <SidebarNavItemMore>
                                <SidebarNavItemCount>
                                    {coeditorCumatrixSize > 0 && (
                                        <NumberFormat
                                            number={coeditorCumatrixSize}
                                            decimal={0}
                                        />
                                    )}
                                </SidebarNavItemCount>
                                <SidebarNavItemMoreIcon>
                                    <i className="zmdi zmdi-chevron-right"></i>
                                </SidebarNavItemMoreIcon>
                            </SidebarNavItemMore> */}
                        </SidebarNavItem>
                    </CupoySrefView>


                    {/* <CupoySrefView
                        statename={
                            CupoyRouter.STATES.MYPAGE_COLLECTIONS_COEDITOR
                        }
                    >
                        <SidebarNavItem
                            isActive={CupoyRouter.isActive(
                                CupoyRouter.STATES.MYPAGE_COLLECTIONS_COEDITOR
                            )}
                        >
                            <SidebarNavItemIcon iconSize={20}>
                                <span className="material-icons-outlined">
                                    library_books
                                </span>
                            </SidebarNavItemIcon>
                            <SidebarNavItemText>
                                <I18N>共同編輯的課程</I18N>
                            </SidebarNavItemText>
                        </SidebarNavItem>
                    </CupoySrefView> */}

                    {/* {selfInfluencer && (
                        <>
                            <CupoySrefView
                                statename={
                                    CupoyRouter.STATES.MYPAGE_COLLECTIONS_MYSELF
                                }
                            >
                                <SidebarNavItem
                                    isActive={CupoyRouter.isActive(
                                        CupoyRouter.STATES
                                            .MYPAGE_COLLECTIONS_MYSELF
                                    )}
                                >
                                    <SidebarNavItemIcon iconSize={20}>
                                        <i className="icon-ticket"></i>
                                    </SidebarNavItemIcon>
                                    <SidebarNavItemText>
                                        <I18N>我製作的網路課程</I18N>
                                    </SidebarNavItemText>
                                </SidebarNavItem>
                            </CupoySrefView>

                            <CupoySrefView
                                statename={
                                    CupoyRouter.STATES.MYPAGE_MARATHON_MYSELF
                                }
                            >
                                <SidebarNavItem
