import styled from 'styled-components';
const NavItemHeight = 40;
const NavTitleHeight = 40;

export const Div0 = styled.div`
    width: 100%;
    height: 100%;
    margin: 0 auto;
    display:grid;
    grid-template-rows: 85px 1fr;

`
export const Title = styled.div`
    display:grid;
    grid-template:1fr / auto 1fr;
    border-bottom:gray 2px solid;
    padding-bottom:10px;
    align-items:end;
`
export const ChooseDiv = styled.div`
    background-color:cyan;
`
export const TitleText = styled.div`
    font-size:30px;
`
export const ChooseTitle =styled.div`
    height: ${NavTitleHeight}px;
    line-height: ${NavTitleHeight}px;
    padding: 0 20px;
    font-size: 16px;
    text-align: left;
    background-color: #f0efee;
    color: rgba(0, 0, 0, 0.5);
    font-weight: 600;
`
export const ChooseList = styled.div`
    padding: 10px
`
export const ChooseItem =styled.div`
    display: flex;
    height: ${NavItemHeight}px;
    padding: 0 10px;
    flex-direction: row;
    justify-content: flex-start;
    align-items: center;
    margin: ${({ margin }) => (margin ? margin : "0")};

    ${(props) => {
        if (props.isActive) {
            return `
                color: ${Colors.Red};
                background-color: rgb(253, 241, 241);
                border-left: 5px solid ${Colors.Red};
                border-radius: 0 20px 20px 0;
            `;
        } else {
            return `
                color: #323030;
                background-color: #FFF;
                &:hover {
                    background-color: rgb(246, 245, 244);
                }
            `;
        }
    }}

    ${SidebarNavItemCount} {
        color: ${(props) => (props.isActive ? Colors.Red : Colors.Dark3)};
    }
`
