import React from "react";
import { Div0,Logo,HeaderList,LogoText,BesideTop,BesideMid,BesideBot } from "./style";
import './sty.css'
const BesideInner = () => {
    var acc = document.getElementsByClassName("accordion");
    var i;
    for (i = 0; i < acc.length; i++) {
        acc[i].addEventListener("click", function() {
            this.classList.toggle("active")
            var panel = this.nextElementSibling
            if (panel.style.display === "block") {
                console.log(13)
                panel.style.display = "none"
            } else {
                panel.style.display = "block"
            }
        });
    }

    return(
        <Div0>

            <BesideTop>
                <Logo>
                    <img src='https://i.imgur.com/BJ21wvu.png'  height={56} width={87.5}></img>
                </Logo>
                <HeaderList>
                    <LogoText>
                        管理系統
                    </LogoText>
                </HeaderList>
            </BesideTop>
            <BesideMid>
            <button className="accordion" >Section 1</button>
            <div className="panel">
                <p>1.</p>
            </div>

            <button className="accordion" >Section 2</button>
            <div className="panel">
                <p>2</p>
            </div>

            <button className="accordion">Section 3</button>
            <div className="panel">
                <p>3</p>
            </div>
            </BesideMid>
            <BesideBot>

            </BesideBot>
        </Div0>
    )
}
export default BesideInner;
